mapper.select:
SELECT s.province_code AS provinceCode,
s.city_code AS cityCode,
SUM(IF(s.so2_value > 100, 1, 0)) AS so2ExcessCount, SUM(IF(s.co_value > 100, 1, 0)) AS coExcessCount, SUM(IF(s.spm_value > 100, 1, 0)) AS spmExcessCount, SUM(IF(s.aqi_id > 2, 1, 0)) AS aqiExcessCount FROM statistics s
WHERE s.province_code = #{provinceCode} GROUP BY s.city_code;
GROUP BY s.province_code;
